sp_widget.client_script:
api.controller = function($rootScope, $scope, spModal, spUtil) {
    /* widget controller */
    var c = this;

    $rootScope.$on('selectNote', function(event, data) {
		console.log("rootdsdjkskfjsdk");
        c.server.get({
            action: 'getNote',
            noteId: $rootScope.noteId
        }).then(function(response) {
			console.log("resp: " + JSON.stringify(response.data));
			console.log("c.data os: " + JSON.stringify(c.data));
			console.log("data os: " + JSON.stringify(data));
            c.data.title = response.data.note.title;
            c.data.note = response.data.note.note;
            c.data.noteId = response.data.note.sys_id;
        });
    });

	c.updateNote = function(updateType){
		c.server.get({
			action: 'updateNote',
			noteId: c.data.noteId,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function(r){
			var updatedData = {
				noteId: c.data.noteId,
				noteTitle: c.data.title, 
				noteBody: c.data.note
			};
			$rootScope.$emit('noteUpdated', updatedData);
		});
	};

	c.confirmDelete = function(){
		spModal.confirm("Are you sure you want to delete this record?").then(function(confirmed){
			if(confirmed){
				c.deleteNote();
			}
		});
	};

	c.deleteNote = function(){
		c.server.get({
			action: 'deleteNote',
			noteId: c.data.noteId
		}).then(function(r){
			spUtil.addTrivialMessage("The " + c.data.title + " record has been deleted");
			$rootScope.$emit('deleteNote', c.data.noteId);
			c.data.title = "";
			c.data.note = "";
			c.data.noteId = "";
		});
	};


};

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    data.ident = "this is the notes body widget";

    if (input && input.noteId) {
		console.log("selectnoteeeeee");
        var noteGr = new GlideRecord("x_snc_createnotes_note");
        if (noteGr.get(input.noteId)) {
            if (input.action == "getNote") {
                data.note = {};
                $sp.getRecordValues(data.note, noteGr, "title,note,sys_id");
				console.log(JSON.stringify(data.note));
            }
			else if(input.action == "updateNote"){
				noteGr.title = input.noteTitle;
				noteGr.note = input.noteBody;
				noteGr.update();
			}

			else if(input.action == 'deleteNote'){
				noteGr.deleteRecord();
			}
        }
    }

})();
